const Facture = require('../models/facture.model');


exports.createFacture = function (req, res) {  
    let facture = new Facture(
        {
            nomUser : req.body.nomUser ,
            prestation: req.body.prestation ,
            FactureH: req.body.FactureH ,
            coutHoraire: req.body.coutHoraire ,
            tva: req.body.tva 
            
        }
    );


    facture.save((err) =>{
        if(err)
        {
            console.log(err);
        }
        else
        {
            console.log("Facture Created");
           
        }
         res.send(facture);
    })
};



exports.facture = function (req, res) {
    Facture.find(req.params.id, function (err, facture) {
        if(err)
        {
            console.log(err);
        }
       res.send(facture);             
    })
};