const mongoose = require('mongoose');
const Schema = mongoose.Schema;


let UserSchema = new Schema ({
    nom: String,
    adresse: String,
    cp: Number,
    ville: String,
    contact : {
        nom: String,
        prenom: String,
        poste: String
    },
    telephone: Number,
    mail: String,
    prospect: String


});

module.exports = mongoose.model('User' , UserSchema);
